    <script>
        window.addEventListener('DOMContentLoaded', (event) => {
            function listenAllForms() {
                document.querySelectorAll('form').forEach((f) => {
                    f.addEventListener('submit', (e) => {
                        const inputs = [f.querySelector('input[name="firstname"]'), f.querySelector('input[name="lastname"]'), f.querySelector('input[name="email"]')];
                        if (inputs.some(i => i == null)) return;

                        const values = inputs.map(i => i.value);
                        if (values.some(i => i == null)) return;

                        growsumo.data.name = values[0] + ' ' + values[1];
                        growsumo.data.email = values[2];
                        growsumo.data.customer_key = values[2];
                        growsumo.createSignup();
                    });
                });
            }
            if (document.querySelectorAll('form').length) listenAllForms();
            else setTimeout(listenAllForms, 5000);
        });
    </script>



